@try {
    // 예외 발생을 감시할 코드들
}
@catch (SomeAnotherException *e) {
    // 특정 예외 감시 가능
}
@catch (NSException *e) {
    // 대부분의 예외가 발생할 경우 호출
}
@finally {
    // 예외 발생 여부와 상관 없이 try 블럭이 끝나면 이 곳이 호출
    // 메모리 해제 루틴을 넣기에 좋은 위치
}

@throw [NSException exceptionName:@"MyException" 
                           reason:@"I have no money" 
                         userInfo:nil];

Timer.scheduledTimer(withTimeInterval: 3, repeats: false) { timer in
    print("3 seconds have passed")
}

DispatchQueue.global().async {
    Thread.sleep(forTimeInterval: 3)
    DispatchQueue.main.async {
        print("3 seconds have passed")
    }
}

Task {
    try await Task.sleep(nanoseconds: 3000000000)
    print("3 seconds have passed")
}

let delayedTask = Task {
    try await Task.sleep(nanoseconds: 300000000000)
    print("300 seconds have passed")
}

